    display: flex;
    gap: 1.2rem;
    align-items: center;
`;

const LoginButton = styled.button`
    padding: 0.8rem 1.8rem;
    background: transparent;
    border: 2px solid #2ABF4A;
    color: #2ABF4A;
    border-radius: 30px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;

    &:hover {
        background: #2ABF4A;
        color: white;
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(42, 191, 74, 0.2);
    }
`;

const SignupButton = styled.button`
    padding: 0.8rem 1.8rem;
    background: #2ABF4A;
    border: none;
    color: white;
    border-radius: 30px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;

    &:hover {
        background: #229F3E;
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(42, 191, 74, 0.2);
    }
`;

const DashboardButton = styled.button`
    padding: 0.8rem 1.8rem;
    background: #2ABF4A;
    border: none;
    color: white;
    border-radius: 30px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;

    &:hover {
        background: #229F3E;
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(42, 191, 74, 0.2);
    }
`;

const LogoutButton = styled.button`
    padding: 0.8rem 1.8rem;
    background: #ff4444;
    border: none;
    color: white;
    border-radius: 30px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 1rem;

    &:hover {
        background: #cc0000;
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(255, 68, 68, 0.2);
    }
`;

