import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Homepage = () => {
    const navigate = useNavigate();
    const { user } = useAuth();
    const [currentSlide, setCurrentSlide] = useState(0);

    const features = [
        {
            title: "AI-Powered Financial Advisor",
            description: "Get personalized financial advice based on your spending patterns and financial goals.",
            image: "https://img.icons8.com/fluency/96/000000/artificial-intelligence.png"
        },
        {
            title: "Transaction Visualization",
            description: "Beautiful charts and graphs to help you understand your spending patterns.",
            image: "https://img.icons8.com/fluency/96/000000/line-chart.png"
        },
        {
            title: "Expense Tracking",
            description: "Easily track and categorize your expenses to maintain a healthy financial life.",
            image: "https://img.icons8.com/fluency/96/000000/money-bag.png"
        },
        {
            title: "Income Management",
            description: "Keep track of all your income sources and manage them effectively.",
            image: "https://img.icons8.com/fluency/96/000000/money.png"
        }
    ];

    const nextSlide = () => {
        setCurrentSlide((currentSlide + 1) % features.length);
    };

    const prevSlide = () => {
        setCurrentSlide((currentSlide - 1 + features.length) % features.length);
    };

    return (
        <HomepageStyled>
            <Navbar>
                <Logo onClick={() => navigate('/')}>
                    <img src="https://img.icons8.com/fluency/48/000000/money-bag.png" alt="Logo" />
                    <h1>PennyPlan</h1>
                </Logo>
                <AuthButtons>
                    {!user ? (
                        <>
                            <LoginButton onClick={() => navigate('/login')}>Login</LoginButton>
                            <SignupButton onClick={() => navigate('/register')}>Sign Up</SignupButton>
                        </>
                    ) : (
                        <DashboardButton onClick={() => navigate('/dashboard')}>Dashboard</DashboardButton>
                    )}
                </AuthButtons>
            </Navbar>

            <HeroSection>
                <h1>Take Control of Your Finances</h1>
                <p>Smart financial management made simple with AI-powered insights</p>
                {!user && (
                    <GetStartedButton onClick={() => navigate('/register')}>
                        Get Started
                    </GetStartedButton>
                )}
            </HeroSection>

            <FeaturesSection>
                <h2>Key Features</h2>
                <Carousel>
                    <CarouselButton onClick={prevSlide}>&lt;</CarouselButton>
                    <CarouselContent>
                        <FeatureCard>
                            <img src={features[currentSlide].image} alt={features[currentSlide].title} />
                            <h3>{features[currentSlide].title}</h3>
                            <p>{features[currentSlide].description}</p>
                        </FeatureCard>
                    </CarouselContent>
                    <CarouselButton onClick={nextSlide}>&gt;</CarouselButton>
                </Carousel>
                <CarouselIndicators>
                    {features.map((_, index) => (
                        <Indicator
                            key={index}
                            active={index === currentSlide}
                            onClick={() => setCurrentSlide(index)}
                        />
                    ))}
                </CarouselIndicators>
            </FeaturesSection>
        </HomepageStyled>
    );
};

const HomepageStyled = styled.div`
    min-height: 100vh;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    padding: 2rem;
`;

const Navbar = styled.nav`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 2rem;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
`;

const Logo = styled.div`
    display: flex;
    align-items: center;
    gap: 1rem;
    cursor: pointer;

    img {
        width: 40px;
        height: 40px;
    }

    h1 {
        color: var(--primary-color);
        font-size: 1.8rem;
        margin: 0;
    }
`;

const AuthButtons = styled.div`
    display: flex;
    gap: 1rem;
`;

const Button = styled.button`
    padding: 0.8rem 1.5rem;
    border-radius: 10px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    border: none;
    font-size: 1rem;

    &:hover {
        transform: translateY(-2px);
    }
`;

const LoginButton = styled(Button)`
    background: transparent;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);

    &:hover {
        background: var(--primary-color);
        color: white;
    }
`;

const SignupButton = styled(Button)`
    background: var(--primary-color);
    color: white;

    &:hover {
        background: var(--color-green);
    }
`;

const DashboardButton = styled(Button)`
    background: var(--color-green);
    color: white;

    &:hover {
        background: var(--primary-color);
    }
`;

const HeroSection = styled.section`
    text-align: center;
    padding: 4rem 2rem;
    margin-top: 2rem;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

    h1 {
        font-size: 3rem;
        color: var(--primary-color);
        margin-bottom: 1rem;
    }

    p {
        font-size: 1.2rem;
        color: #666;
        margin-bottom: 2rem;
    }
`;

const GetStartedButton = styled(Button)`
    background: var(--primary-color);
    color: white;
    padding: 1rem 2rem;
    font-size: 1.2rem;

    &:hover {
        background: var(--color-green);
    }
`;

const FeaturesSection = styled.section`
    padding: 4rem 2rem;
    text-align: center;

    h2 {
        font-size: 2.5rem;
        color: var(--primary-color);
        margin-bottom: 3rem;
    }
`;

const Carousel = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 2rem;
    margin-bottom: 2rem;
`;

const CarouselButton = styled.button`
    background: var(--primary-color);
    color: white;
    border: none;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    font-size: 1.5rem;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;

    &:hover {
        background: var(--color-green);
        transform: scale(1.1);
    }
`;

const CarouselContent = styled.div`
    flex: 1;
    max-width: 600px;
`;

const FeatureCard = styled.div`
    background: white;
    padding: 2rem;
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    transition: all 0.3s ease;

    img {
        width: 80px;
        height: 80px;
        margin-bottom: 1rem;
    }

    h3 {
        color: var(--primary-color);
        font-size: 1.5rem;
        margin-bottom: 1rem;
    }

    p {
        color: #666;
        font-size: 1.1rem;
        line-height: 1.6;
    }
`;

const CarouselIndicators = styled.div`
    display: flex;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 2rem;
`;

const Indicator = styled.div`
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: ${props => props.active ? 'var(--primary-color)' : '#ccc'};
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
        transform: scale(1.2);
    }
`;

export default Homepage;
