import React, { useState, useEffect } from "react";
import { useAuth } from "../../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import styled from "styled-components";

// Declare google as a global variable for ESLint
/* global google */

const BACKEND_URL = 'http://localhost:5000';

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const { register, error, setUser, setIsAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [googleError, setGoogleError] = useState(null);

  const handleGoogleResponse = async (response) => {
    try {
      console.log("Google response received:", response);

      if (!response.credential) {
        console.error("No credential received from Google");
        setGoogleError("No credential received from Google");
        return;
      }

      const res = await fetch(`${BACKEND_URL}/api/v1/auth/google/verify`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          token: response.credential
        })
      });

      console.log("Backend response status:", res.status);
      const data = await res.json();
      console.log("Backend response data:", data);

      if (!res.ok) {
        throw new Error(data.message || 'Failed to verify Google token');
      }

      if (data.token) {
        console.log("Setting auth data...");
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsAuthenticated(true);
        console.log("Auth data set, navigating to dashboard...");
        navigate('/dashboard');
      } else {
        throw new Error("No token received from backend");
      }
    } catch (error) {
      console.error('Google sign-in error:', error);
      setGoogleError(error.message);
    }
  };

  useEffect(() => {
    const initializeGoogleSignIn = () => {
      if (window.google && window.google.accounts) {
        window.google.accounts.id.initialize({
          client_id: "596079188368-2hh5360q26vpb2c4dv9tm3vv3v4jsiso.apps.googleusercontent.com",
          callback: handleGoogleResponse,
          auto_select: true,
        });

        window.google.accounts.id.renderButton(
          document.getElementById("google-signin-button"),
          { 
            theme: "outline", 
            size: "large", 
            width: "100%",
            text: "signup_with",
            shape: "rectangular",
          }
        );

        // Also display the One Tap dialog
        window.google.accounts.id.prompt();
      }
    };

    // Check if Google Identity Services script is already loaded
    if (window.google && window.google.accounts) {
      initializeGoogleSignIn();
    } else {
      // If not loaded, wait for it to load
      const script = document.querySelector('script[src="https://accounts.google.com/gsi/client"]');
      if (script) {
        script.onload = initializeGoogleSignIn;
      }
    }

    // Cleanup function
    return () => {
      if (window.google && window.google.accounts) {
        window.google.accounts.id.cancel();
      }
    };
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match");
      return;
    }

    const success = await register(
      formData.username,
      formData.email,
      formData.password
    );
    if (success) {
      navigate("/dashboard");
    }
  };

  return (
    <RegisterStyled>
      <div className="register-container">
        <div className="register-content">
          <h2>Create your account</h2>
          {error && <div className="error-message">{error}</div>}
          <form onSubmit={handleSubmit}>
            <div className="input-control">
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
                required
              />
            </div>
            <div className="input-control">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="input-control">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <div className="password-requirements">
                <p>Password must:</p>
                <ul>
                  <li>Be at least 6 characters long</li>
                  <li>Contain at least one uppercase letter</li>
                  <li>Contain at least one number</li>
                  <li>Contain at least one special character</li>
                </ul>
              </div>
            </div>
            <div className="input-control">
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm Password"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
            </div>
            <div className="submit-btn">
              <button type="submit">Register</button>
            </div>
          </form>
          <div className="divider">
            <span>OR</span>
          </div>
          <div className="google-btn">
            <div id="google-signin-button"></div>
          </div>
          <div className="login-link">
            <p>
              Already have an account? <Link to="/login">Login</Link>
            </p>
          </div>
        </div>
      </div>
    </RegisterStyled>
  );
};

const RegisterStyled = styled.div`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(252, 246, 249, 0.78);

