      top: 50%;
      width: 45%;
      height: 1px;
      background: #ddd;
    }

    &::before {
      left: 0;
    }

    &::after {
      right: 0;
    }

    span {
      background: white;
      padding: 0 10px;
      color: #666;
      font-size: 0.9rem;
    }
  }

  .google-btn {
    margin-bottom: 1rem;

    .google-signin {
      width: 100%;
      padding: 0.8rem;
      background: white;
      color: #444;
      border: 1px solid #ddd;
      border-radius: 8px;
      font-size: 1rem;
      cursor: pointer;
      transition: all 0.3s ease;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;

      img {
        width: 24px;
        height: 24px;
      }

      &:hover {
        background: #f8f8f8;
        border-color: #aaa;
      }
    }
  }
`;

export default Register;
