import React, { useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { comment } from '../../utils/Icons';

function AISuggestions() {
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');
    const [loading, setLoading] = useState(false);
    const { incomes, expenses } = useGlobalContext();

    const askAI = async () => {
        setLoading(true);
        try {
            // Prepare context about user's financial data
            const financialContext = {
                totalIncomes: incomes.reduce((acc, inc) => acc + inc.amount, 0),
                totalExpenses: expenses.reduce((acc, exp) => acc + exp.amount, 0),
                recentExpenses: expenses.slice(0, 5).map(exp => ({
                    category: exp.category,
                    amount: exp.amount
                })),
                expensesByCategory: expenses.reduce((acc, exp) => {
                    acc[exp.category] = (acc[exp.category] || 0) + exp.amount;
                    return acc;
                }, {})
            };

            const response = await fetch('https://api.groq.com/openai/v1/chat/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer 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'
                },
                body: JSON.stringify({
                    model: "meta-llama/llama-4-scout-17b-16e-instruct",
                    messages: [
                        {
                            role: "system",
                            content: "You are a financial advisor AI. Analyze the provided financial data and give specific, actionable advice."
                        },
                        {
                            role: "user",
                            content: `Based on this financial data: ${JSON.stringify(financialContext)}, please answer: ${question}`
                        }
                    ]
                })
            });

            const data = await response.json();
            setAnswer(data.choices[0].message.content);
        } catch (error) {
            setAnswer("Sorry, I couldn't process your request at the moment.");
            console.error('Error:', error);
        }
        setLoading(false);
    };

    return (
        <AISuggestionsStyled>
            <h2>AI Financial Advisor</h2>
            <div className="input-container">
                <input 
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Ask about your finances..."
                />
                <button 
                    onClick={askAI}
                    disabled={loading || !question.trim()}
                >
                    {loading ? 'Thinking...' : 'Ask AI'}
                </button>
            </div>
            {answer && (
                <div className="answer">
                    <div className="icon">{comment}</div>
                    <p>{answer}</p>
                </div>
            )}
        </AISuggestionsStyled>
    );
}

const AISuggestionsStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    border-radius: 20px;
    padding: 2rem;
    margin-top: 2rem;
    width: 100%;

    h2 {
        font-size: 1.5rem;
        margin-bottom: 1.5rem;
        color: var(--primary-color);
    }

    .input-container {
        display: flex;
        gap: 1rem;
        margin-bottom: 1.5rem;

        input {
            flex: 1;
            padding: 1rem;
            border: 2px solid #fff;
            border-radius: 10px;
            font-size: 1.1rem;
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            outline: none;
            
            &:focus {
                border-color: var(--primary-color);
            }
        }

        button {
            padding: 0 2rem;
            border: none;
            background: var(--primary-color);
            color: white;
            border-radius: 10px;
            font-size: 1.1rem;
            cursor: pointer;
            transition: all .3s ease;

            &:hover {
                background: var(--color-green);
            }

            &:disabled {
                background: #ccc;
                cursor: not-allowed;
            }
        }
    }

    .answer {
        background: white;
        padding: 1.5rem;
        border-radius: 10px;
        display: flex;
        gap: 1rem;
        align-items: flex-start;

        .icon {
            background: #F5F5F5;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            i {
                font-size: 1.5rem;
                color: var(--primary-color);
            }
        }

        p {
            flex: 1;
            line-height: 1.6;
            color: var(--primary-color);
        }
    }
`;

export default AISuggestions;
