import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Homepage = () => {
    const navigate = useNavigate();
    const { user } = useAuth();
    const [currentSlide, setCurrentSlide] = useState(0);

    const features = [
        {
            title: "AI-Powered Financial Advisor",
            description: "Get personalized financial advice based on your spending patterns and financial goals.",
            image: "https://img.icons8.com/fluency/96/000000/artificial-intelligence.png"
        },
        {
            title: "Transaction Visualization",
            description: "Beautiful charts and graphs to help you understand your spending patterns.",
            image: "https://img.icons8.com/fluency/96/000000/line-chart.png"
        },
        {
            title: "Expense Tracking",
            description: "Easily track and categorize your expenses to maintain a healthy financial life.",
            image: "https://img.icons8.com/fluency/96/000000/money-bag.png"
        },
        {
            title: "Income Management",
            description: "Keep track of all your income sources and manage them effectively.",
            image: "https://img.icons8.com/fluency/96/000000/money.png"
        }
    ];

    // Auto-rotate carousel
    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentSlide((currentSlide + 1) % features.length);
        }, 5000);
        return () => clearInterval(interval);
    }, [currentSlide, features.length]);

    return (
        <HomepageStyled>
            <Navbar>
                <Logo onClick={() => navigate('/')}>
                    <img src="https://img.icons8.com/fluency/48/000000/money-bag.png" alt="Logo" />
                    <h1>PennyPlan</h1>
                </Logo>
                <AuthButtons>
                    {!user ? (
                        <>
                            <LoginButton onClick={() => navigate('/login')}>Sign In</LoginButton>
                            <SignupButton onClick={() => navigate('/register')}>Sign Up</SignupButton>
                        </>
                    ) : (
                        <>
                            <DashboardButton onClick={() => navigate('/dashboard')}>Dashboard</DashboardButton>
                            <LogoutButton onClick={() => {
                                localStorage.removeItem('token');
                                localStorage.removeItem('user');
                                navigate('/');
                                window.location.reload();
                            }}>Sign Out</LogoutButton>
                        </>
                    )}
                </AuthButtons>
            </Navbar>

            <HeroSection>
                <h1>Take Control of Your Finances</h1>
                <p>Smart financial management made simple with AI-powered insights</p>
                {!user && (
                    <GetStartedButton onClick={() => navigate('/register')}>
                        Get Started
                    </GetStartedButton>
                )}
            </HeroSection>

            <FeaturesSection>
                <h2>Key Features</h2>
                <Carousel>
                    <CarouselContent>
                        <FeatureCard>
                            <img src={features[currentSlide].image} alt={features[currentSlide].title} />
                            <h3>{features[currentSlide].title}</h3>
                            <p>{features[currentSlide].description}</p>
                        </FeatureCard>
                    </CarouselContent>
                </Carousel>
                <CarouselIndicators>
                    {features.map((_, index) => (
                        <Indicator
                            key={index}
                            active={index === currentSlide}
                            onClick={() => setCurrentSlide(index)}
                        />
                    ))}
                </CarouselIndicators>
            </FeaturesSection>

            <AboutSection>
                <h2>About PennyPlan</h2>
                <p>
                    PennyPlan is your personal financial management assistant that helps you track expenses,
                    manage income, and make informed financial decisions. Our AI-powered platform provides
                    personalized insights and recommendations to help you achieve your financial goals.
                </p>
                <FeaturesGrid>
                    <FeatureItem>
                        <img src="https://img.icons8.com/fluency/48/000000/security-checked.png" alt="Secure" />
                        <h3>Secure & Private</h3>
                        <p>Your financial data is encrypted and protected</p>
                    </FeatureItem>
                    <FeatureItem>
                        <img src="https://img.icons8.com/fluency/48/000000/cloud-sync.png" alt="Cloud Sync" />
                        <h3>Cloud Sync</h3>
                        <p>Access your data from anywhere, anytime</p>
                    </FeatureItem>
                    <FeatureItem>
                        <img src="https://img.icons8.com/fluency/48/000000/analytics.png" alt="Analytics" />
                        <h3>Smart Analytics</h3>
                        <p>Get detailed insights into your spending patterns</p>
                    </FeatureItem>
                </FeaturesGrid>
            </AboutSection>

            <ContactSection>
                <h2>Contact Us</h2>
                <ContactInfo>
                    <ContactItem>
                        <img src="https://img.icons8.com/fluency/48/000000/email.png" alt="Email" />
                        <p>support@pennyplan.com</p>
                    </ContactItem>
                    <ContactItem>
                        <img src="https://img.icons8.com/fluency/48/000000/phone.png" alt="Phone" />
                        <p>+1 (555) 123-4567</p>
                    </ContactItem>
                    <ContactItem>
                        <img src="https://img.icons8.com/fluency/48/000000/location.png" alt="Location" />
                        <p>123 Finance Street, Money City</p>
                    </ContactItem>
                </ContactInfo>
            </ContactSection>
        </HomepageStyled>
    );
};

const HomepageStyled = styled.div`
    min-height: 100vh;
    background: 
        linear-gradient(135deg, rgba(76, 175, 80, 0.1) 0%, rgba(129, 199, 132, 0.1) 100%),
        url('https://images.unsplash.com/photo-1554224155-6726b3ff858f?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    position: relative;
    overflow: hidden;

    &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(255, 255, 255, 0.85);
        z-index: -1;
    }

    .content {
        position: relative;
        z-index: 1;
        padding: 2rem;
        max-width: 1200px;
        margin: 0 auto;
    }

    .hero {
        text-align: center;
        padding: 4rem 0;
        color: #222260;

        h1 {
            font-size: 3rem;
            margin-bottom: 1rem;
            background: linear-gradient(135deg, #4CAF50 0%, #81C784 100%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        p {
            font-size: 1.2rem;
            max-width: 600px;
            margin: 0 auto;
        }
    }

    .features {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
        gap: 2rem;
        padding: 2rem 0;
    }

    .feature-card {
        background: rgba(255, 255, 255, 0.95);
        padding: 2rem;
        border-radius: 20px;
        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
        transition: all 0.3s ease;

        &:hover {
            transform: translateY(-5px);
            box-shadow: 0 12px 40px rgba(0, 0, 0, 0.15);
        }

        h3 {
            color: #222260;
            margin-bottom: 1rem;
        }

        p {
            color: #666;
        }
    }

    .cta {
        text-align: center;
        padding: 3rem 0;

        button {
            background: linear-gradient(135deg, #4CAF50 0%, #81C784 100%);
            color: white;
            padding: 1rem 2rem;
            border-radius: 8px;
            font-size: 1.2rem;
            border: none;
            cursor: pointer;
            transition: all 0.3s ease;

            &:hover {
                transform: translateY(-2px);
                box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
            }
        }
    }
`;

const Navbar = styled.nav`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 2rem;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
`;

const Logo = styled.div`
    display: flex;
    align-items: center;
    gap: 1rem;
    cursor: pointer;

    img {
        width: 40px;
        height: 40px;
    }

    h1 {
        color: var(--primary-color);
        font-size: 1.8rem;
        margin: 0;
    }
`;

const AuthButtons = styled.div`
    display: flex;
    gap: 1rem;
`;

const Button = styled.button`
    padding: 0.8rem 1.5rem;
    border-radius: 10px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    border: none;
    font-size: 1rem;

    &:hover {
        transform: translateY(-2px);
    }
`;

const LoginButton = styled(Button)`
    background: transparent;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);

    &:hover {
        background: var(--primary-color);
        color: white;
    }
`;

const SignupButton = styled(Button)`
    background: var(--primary-color);
    color: white;

    &:hover {
        background: var(--color-green);
    }
`;

const DashboardButton = styled(Button)`
    background: var(--color-green);
    color: white;

    &:hover {
        background: var(--primary-color);
    }
`;

const LogoutButton = styled(Button)`
    background: var(--color-accent);
    color: white;

    &:hover {
        background: var(--color-delete);
    }
`;

const HeroSection = styled.section`
    text-align: center;
    padding: 4rem 2rem;
    margin-top: 2rem;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

    h1 {
        font-size: 3rem;
        color: var(--primary-color);
        margin-bottom: 1rem;
    }

    p {
        font-size: 1.2rem;
        color: #666;
        margin-bottom: 2rem;
    }
`;

const GetStartedButton = styled(Button)`
    background: var(--primary-color);
    color: white;
    padding: 1rem 2rem;
    font-size: 1.2rem;

    &:hover {
        background: var(--color-green);
    }
`;

const FeaturesSection = styled.section`
    padding: 4rem 2rem;
    text-align: center;

    h2 {
        font-size: 2.5rem;
        color: var(--primary-color);
        margin-bottom: 3rem;
    }
`;

const Carousel = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 2rem;
`;

const CarouselContent = styled.div`
    flex: 1;
    max-width: 600px;
`;

const FeatureCard = styled.div`
    background: white;
    padding: 2rem;
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    transition: all 0.3s ease;

    img {
        width: 80px;
        height: 80px;
        margin-bottom: 1rem;
    }

    h3 {
        color: var(--primary-color);
        font-size: 1.5rem;
        margin-bottom: 1rem;
    }

    p {
        color: #666;
        font-size: 1.1rem;
        line-height: 1.6;
    }
`;

const CarouselIndicators = styled.div`
    display: flex;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 2rem;
`;

const Indicator = styled.div`
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: ${props => props.active ? 'var(--primary-color)' : '#ccc'};
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
        transform: scale(1.2);
    }
`;

const AboutSection = styled.section`
    padding: 4rem 2rem;
    text-align: center;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 20px;
    margin: 2rem 0;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

    h2 {
        font-size: 2.5rem;
        color: var(--primary-color);
        margin-bottom: 2rem;
    }

    p {
        font-size: 1.2rem;
        color: #666;
        max-width: 800px;
        margin: 0 auto 3rem;
        line-height: 1.6;
    }
`;

const FeaturesGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    max-width: 1200px;
    margin: 0 auto;
`;

const FeatureItem = styled.div`
    padding: 2rem;
    background: white;
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    transition: all 0.3s ease;

    &:hover {
        transform: translateY(-5px);
    }

    img {
        width: 48px;
        height: 48px;
        margin-bottom: 1rem;
    }

    h3 {
        color: var(--primary-color);
        font-size: 1.5rem;
        margin-bottom: 1rem;
    }

    p {
        color: #666;
        font-size: 1.1rem;
        line-height: 1.6;
    }
`;

const ContactSection = styled.section`
    padding: 4rem 2rem;
    text-align: center;

    h2 {
        font-size: 2.5rem;
        color: var(--primary-color);
        margin-bottom: 3rem;
    }
`;

const ContactInfo = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    max-width: 1200px;
    margin: 0 auto;
`;

const ContactItem = styled.div`
    padding: 2rem;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 20px;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    transition: all 0.3s ease;

    &:hover {
        transform: translateY(-5px);
    }

    img {
        width: 48px;
        height: 48px;
        margin-bottom: 1rem;
    }

    p {
        color: #666;
        font-size: 1.1rem;
        line-height: 1.6;
    }
`;

export default Homepage;
